    private void ItemLoadBar100() {
        int i = 0;
        try {
            while (i <= 100) {
                // fill the menu bar
                ItemProgressBar.setValue(i + 10);
 
                // delay the thread
                Thread.sleep(250);
                i += 10;
            }
        }
        catch (Exception e) {
        }
    }
